import React, { useState, useEffect, useCallback } from "react";
import "./App.css";

const basicButtons = [
  ["7", "8", "9", "/"],
  ["4", "5", "6", "*"],
  ["1", "2", "3", "-"],
  ["0", ".", "=", "+"]
];

const scientificButtons = [
  ["sin", "cos", "tan", "log"],
  ["‚àö", "x¬≤", "x¬≥", "x^y"],
  ["œÄ", "e", "!", "1/x"],
  ["(", ")", "^", "%"]
];

function App() {
  const [mode, setMode] = useState("basic");
  const [display, setDisplay] = useState("");
  const [history, setHistory] = useState([]);

  const handleButtonClick = useCallback((value) => {
    if (value === "=") {
      try {
        // eslint-disable-next-line no-eval
        const result = eval(display).toString();
        setHistory((prev) => [...prev, { expr: display, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (["sin", "cos", "tan", "log"].includes(value)) {
      try {
        const num = parseFloat(display);
        let result = "";
        switch (value) {
          case "sin":
            result = Math.sin(num).toString();
            break;
          case "cos":
            result = Math.cos(num).toString();
            break;
          case "tan":
            result = Math.tan(num).toString();
            break;
          case "log":
            result = Math.log10(num).toString();
            break;
          default:
            break;
        }
        setHistory((prev) => [...prev, { expr: `${value}(${display})`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "œÄ") {
      setDisplay(display + Math.PI.toString());
    } else if (value === "e") {
      setDisplay(display + Math.E.toString());
    } else if (value === "‚àö") {
      try {
        const num = parseFloat(display);
        const result = Math.sqrt(num).toString();
        setHistory((prev) => [...prev, { expr: `‚àö(${display})`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "x¬≤") {
      try {
        const num = parseFloat(display);
        const result = (num * num).toString();
        setHistory((prev) => [...prev, { expr: `${display}¬≤`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "x¬≥") {
      try {
        const num = parseFloat(display);
        const result = (num * num * num).toString();
        setHistory((prev) => [...prev, { expr: `${display}¬≥`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "1/x") {
      try {
        const num = parseFloat(display);
        const result = (1 / num).toString();
        setHistory((prev) => [...prev, { expr: `1/${display}`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "!") {
      try {
        const num = parseInt(display);
        if (num < 0) {
          setDisplay("Error");
          return;
        }
        let factorial = 1;
        for (let i = 2; i <= num; i++) {
          factorial *= i;
        }
        const result = factorial.toString();
        setHistory((prev) => [...prev, { expr: `${display}!`, result }]);
        setDisplay(result);
      } catch {
        setDisplay("Error");
      }
    } else if (value === "C") {
      setDisplay("");
    } else {
      setDisplay(display + value);
    }
  }, [display]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key;
      if ((/^[0-9]$/).test(key)) {
        setDisplay((prev) => prev + key);
      } else if (["+", "-", "*", "/", ".", "%", "(", ")", "^"].includes(key)) {
        setDisplay((prev) => prev + key);
      } else if (key === "Enter" || key === "=") {
        handleButtonClick("=");
      } else if (key === "Backspace") {
        setDisplay((prev) => prev.slice(0, -1));
      } else if (key.toLowerCase() === "c") {
        setDisplay("");
      }
      // Scientific functions via keyboard
      else if (mode === "scientific") {
        if (key.toLowerCase() === "s") handleButtonClick("sin");
        else if (key.toLowerCase() === "o") handleButtonClick("cos");
        else if (key.toLowerCase() === "t") handleButtonClick("tan");
        else if (key.toLowerCase() === "l") handleButtonClick("log");
        else if (key.toLowerCase() === "p") handleButtonClick("œÄ");
        else if (key.toLowerCase() === "e") handleButtonClick("e");
        else if (key === "r") handleButtonClick("‚àö");
        else if (key === "2") handleButtonClick("x¬≤");
        else if (key === "3") handleButtonClick("x¬≥");
        else if (key === "i") handleButtonClick("1/x");
        else if (key === "f") handleButtonClick("!");
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [display, mode, handleButtonClick]);

  return (
    <div className="app-layout">
      <div className="calculator-container">
        <h1>Calculator</h1>
        <div className="mode-toggle">
          <button
            className={mode === "basic" ? "active" : ""}
            onClick={() => setMode("basic")}
          >
            Basic
          </button>
          <button
            className={mode === "scientific" ? "active" : ""}
            onClick={() => setMode("scientific")}
          >
            Scientific
          </button>
        </div>
        <div className="display">{display || "0"}</div>
        <div className="buttons">
          {(mode === "scientific" ? scientificButtons : []).map((row, i) => (
            <div className="button-row" key={"sci-" + i}>
              {row.map((btn) => (
                <button key={btn} onClick={() => handleButtonClick(btn)}>
                  {btn}
                </button>
              ))}
            </div>
          ))}
          {basicButtons.map((row, i) => (
            <div className="button-row" key={i}>
              {row.map((btn) => (
                <button key={btn} onClick={() => handleButtonClick(btn)}>
                  {btn}
                </button>
              ))}
            </div>
          ))}
          <div className="button-row">
            <button onClick={() => handleButtonClick("C")}>C</button>
          </div>
        </div>
      </div>
      <div className="history-panel">
        <div className="history-header">
          <h2>History</h2>
          <button className="clear-history-btn" onClick={() => setHistory([])} aria-label="Clear history">üóëÔ∏è</button>
        </div>
        <ul>
          {history.slice().reverse().map((item, idx) => (
            <li key={idx}>
              <div className="history-expr">{item.expr}</div>
              <div className="history-result">= {item.result}</div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
